import React, { useState } from "react";

// In real app, this could be moved to a context or global store
let adminName = ""; // ⬅️ Temporary global variable

const Admin = () => {
  const [name, setName] = useState("");

  const handleChange = (e) => {
    setName(e.target.value);
    adminName = e.target.value; // Store it globally
    console.log("Admin name set to:", adminName); // For testing
  };

  return (
    <div className="min-h-screen bg-gray-900 flex items-center justify-center p-4">
      <div className="max-w-md bg-white shadow-md rounded-md p-6 w-full">
        <h1 className="text-2xl font-bold text-teal-800 mb-4 text-center">
          Admin View
        </h1>
        <label className="block text-gray-700 font-semibold mb-2">
          Sisesta administraatori nimi:
        </label>
        <input
          type="text"
          value={name}
          onChange={handleChange}
          className="w-full px-4 py-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-teal-500"
          placeholder="Näiteks: Mari"
        />
      </div>
    </div>
  );
};

export default Admin;

// Exporting the global value for testing or cross-file access (not ideal long-term)
export { adminName };
