import React, { useState } from "react";

// Temporary in-memory store — you can export or replace with context/localStorage later
let storedQuestion = "";

const Admin = () => {
  const [question, setQuestion] = useState("");

  const handleSubmit = () => {
    storedQuestion = question;
    console.log("Küsimus salvestatud:", storedQuestion);
    // You can also add a toast or alert here to show confirmation
  };

  return (
    <div className="min-h-screen bg-gray-900 flex items-center justify-center p-4">
      <div className="container max-w-md bg-white shadow-md rounded-md p-6">
        <h1 className="text-2xl font-bold text-teal-800 mb-8 text-center">
            Sisesta hääletusele pandav küsimus.
        </h1>
        <p className="text-xl text-gray-800 text-center mb-8">
            Peale vajutust nupule Salvesta Küsimus kuvatakse esilehel küsimus ja käivitub 
            5 minutit vastamiseks.
        </p>
        <form>
          <div className="mb-4">
            <label className="block text-teal-800 font-semibold mb-1" htmlFor="question">
              Küsimus
            </label>
            <textarea
              id="question"
              name="question"
              rows={4}
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded resize-none"
              placeholder="Sisesta küsimus"
            />
          </div>
          <button
            type="button"
            onClick={handleSubmit}
            className="mb-6 w-full bg-teal-500 border border-green-800 px-4 py-2 text-white text-xl rounded hover:bg-blue-700 transition"
          >
            Salvesta küsimus
          </button>
        </form>
      </div>
    </div>
  );
};

export default Admin;

// Export the stored question if needed elsewhere
export { storedQuestion };
